// Get all the data from previous nodes
const contactDataRaw = $node["HTTP Request - Contact"].json;
const opportunities = $node["HTTP Request - Get Opportunities"].json.data || [];
const communications = $node["HTTP Request - Get Communications"].json.data || [];

// Extract contacts array
const contacts = contactDataRaw.data || [];

// Find first contact with an email
const contactWithEmail = contacts.find(c => Array.isArray(c.emails) && c.emails.length > 0 && c.emails[0].email);

// Find first contact with a phone
const contactWithPhone = contacts.find(c => Array.isArray(c.phones) && c.phones.length > 0 && c.phones[0].phone);

// Extract email and phone
const email = contactWithEmail?.emails?.[0]?.email || '';
const phone = contactWithPhone?.phones?.[0]?.phone || '';

// Fallback contact (for name, id, lead_id)
const fallbackContact = contactWithEmail || contactWithPhone || contacts[0] || {};
const leadId = fallbackContact.lead_id || '';

// Process opportunity data
const primaryOpp = opportunities[0] || {};

// ✅ Fix: Use _type instead of type
const emails = communications.filter(c => c._type?.toLowerCase() === 'email');
const sms = communications.filter(c => ['sms', 'sms_reply'].includes(c._type?.toLowerCase()));

// Email and SMS counts
const emailCount = emails.length;
const smsCount = sms.length;

// Get recent message content
const recentEmails = emails.slice(0, 3).map(e => ({
  direction: e.direction,
  date: e.date_created,
  content: (e.envelope?.subject || 'No subject') + ' - ' + (e.body ? e.body.substring(0, 150) + '...' : 'No content')
}));

const recentSMS = sms.slice(0, 5).map(s => ({
  direction: s.direction,
  date: s.date_created,
  content: s.text || 'No content'
}));

// Communication analysis
const preferredChannel = smsCount > emailCount ? 'SMS' : 'Email';
const lastContactDate = communications[0]?.date_created;
const daysSinceContact = lastContactDate
  ? Math.floor((new Date() - new Date(lastContactDate)) / (1000 * 60 * 60 * 24))
  : 999;

// Build enhanced context for ChatGPT
const enhancedContext = `LEAD ANALYSIS & FOLLOW-UP REQUEST

Contact: ${fallbackContact.display_name || fallbackContact.name || 'Unknown'}
Email: ${email || 'No email'}
Phone: ${phone || 'No phone'}

OPPORTUNITY DETAILS:
- Value: $${primaryOpp.value || 0}
- Stage: ${primaryOpp.status_label || 'No stage'}
- Description: ${primaryOpp.note || 'No opportunity description available'}

COMMUNICATION HISTORY:
- Total Emails: ${emailCount}
- Total SMS Messages: ${smsCount}
- Preferred Channel Based on History: ${preferredChannel}
- Days Since Last Contact: ${daysSinceContact}

RECENT EMAIL CONVERSATIONS:
${recentEmails.length > 0 ? recentEmails.map(e => `${e.direction}: ${e.content} (${e.date})`).join('\n') : 'No recent emails'}

RECENT SMS CONVERSATIONS:
${recentSMS.length > 0 ? recentSMS.map(s => `${s.direction}: ${s.content} (${s.date})`).join('\n') : 'No recent SMS messages'}

TASK: Analyze this lead and determine the best follow-up approach based on the OPPORTUNITY DESCRIPTION above.

Consider:
1. Communication preferences and response patterns
2. Opportunity stage and urgency
3. Recent conversation context
4. Time since last contact
5. Deal value and professionalism needed
6. The specific opportunity description provided

Respond in this EXACT JSON format:
{
  "recommended_channel": "SMS" or "Email",
  "message": "Your follow-up message here",
  "reasoning": "Brief explanation of why you chose this channel"
}`;

return [{
  json: {
    name: fallbackContact.display_name || fallbackContact.name || '',
    email,
    phone,
    contact_id: fallbackContact.id || '',
    lead_id: leadId,
    enhanced_prompt: enhancedContext,
    preferred_channel: preferredChannel,
    opportunity_value: primaryOpp.value || 0,
    opportunity_description: primaryOpp.note || '',
    days_since_contact: daysSinceContact
  }
}];

// Parse the ChatGPT response - handle different response formats
let aiResponse;

try {
  // Try the standard OpenAI API format first
  if ($node["OpenAI"].json.choices && $node["OpenAI"].json.choices[0]) {
    aiResponse = $node["OpenAI"].json.choices[0].message.content;
  } 
  // Try alternative format
  else if ($node["OpenAI"].json.message && $node["OpenAI"].json.message.content) {
    aiResponse = $node["OpenAI"].json.message.content;
  }
  // Try direct content format
  else if ($node["OpenAI"].json.content) {
    aiResponse = $node["OpenAI"].json.content;
  }
  // Fallback - use the entire response as string
  else {
    aiResponse = JSON.stringify($node["OpenAI"].json);
  }
} catch (error) {
  aiResponse = "Error reading AI response";
}

let parsedResponse;
try {
  // Try to parse as JSON
  parsedResponse = JSON.parse(aiResponse);
} catch (error) {
  // If JSON parsing fails, treat as plain text message and default to SMS
  parsedResponse = {
    recommended_channel: "SMS",
    message: aiResponse,
    reasoning: "Default to SMS - AI response was not in JSON format"
  };
}

// Get contact info and lead ID for routing
const contactData = $node["Code - Merge Lead Data"].json;

return [{
  json: {
    channel: parsedResponse.recommended_channel,
    message: parsedResponse.message,
    reasoning: parsedResponse.reasoning,
    contact_name: contactData.name,
    contact_email: contactData.email,
    contact_phone: contactData.phone,
    contact_id: contactData.contact_id,
    lead_id: contactData.lead_id   // ✅ Include lead ID for Close.com
  }
}];
